<script>
    function confirmBackup() {
        swal({
            title: "Confirmation",
            text: "Are you sure you want to perform the backup?",
            icon: "warning",
            buttons: ["Cancel", "OK"],
            dangerMode: true,
        }).then(function (confirmed) {
            if (confirmed) {
                window.location.href = "./?page=backupesu";
            }
        });

        return false; // Prevents the default link behavior
    }
    function confirmRestore() {
        swal({
            title: "Confirmation",
            text: "Are you sure you want to perform the restore?",
            icon: "warning",
            buttons: ["Cancel", "OK"],
            dangerMode: true,
        }).then(function (confirmed) {
            if (confirmed) {
                window.location.href = "./?page=restoreesu";
            }
        });

        return false; // Prevents the default link behavior
    }
</script>
